mapper.select:
select count(1) as count from app_info

	
		and softwareName like CONCAT ('%',#{softwareName},'%')
	
	
		and status = #{status}
	
	
		and flatformId = #{flatformId}
	
	
		and categoryLevel1 = #{categoryLevel1}
	
	
		and categoryLevel2 = #{categoryLevel2}
	
	
		and categoryLevel3 = #{categoryLevel3}

SELECT i.*, v.versionNo, c1.categoryName AS categoryLevel1Name, c2.categoryName AS categoryLevel2Name, c3.categoryName AS categoryLevel3Name, d1.valueName AS statusName, d2.valueName AS flatformName
FROM app_info i LEFT JOIN app_version v ON i.versionId = v.id LEFT JOIN app_category c1 ON i.categoryLevel1 = c1.id LEFT JOIN app_category c2 ON i.categoryLevel2 = c2.id LEFT JOIN app_category c3 ON i.categoryLevel3 = c3.id LEFT JOIN data_dictionary d1 ON i.status = d1.valueId LEFT JOIN data_dictionary d2 ON i.flatformId = d2.valueId
WHERE d1.typeCode = "APP_STATUS" AND d2.typeCode = "APP_FLATFORM"
and i.softwareName like CONCAT ('%',#{softwareName},'%')
and i.status = #{status}
and i.flatformId = #{flatformId}
and i.categoryLevel1 = #{categoryLevel1}
and i.categoryLevel2 = #{categoryLevel2}
and i.categoryLevel3 = #{categoryLevel3}
order by i.creationDate DESC limit #{from},#{pageSize}

mapper.update:
update app_info
softwareName = #{softwareName}, supportROM = #{supportROM}, interfaceLanguage = #{interfaceLanguage}, softwareSize = #{softwareSize}, updateDate = #{updateDate}, devId = #{devId}, status = #{status}, onSaleDate = #{onSaleDate}, offSaleDate = #{offSaleDate}, downloads = #{downloads}, flatformId = #{flatformId}, categoryLevel1 = #{categoryLevel1}, categoryLevel2 = #{categoryLevel2}, categoryLevel3 = #{categoryLevel3}, appInfo = #{appInfo}, logoPicPath = #{logoPicPath}, logoLocPath = #{logoLocPath}, modifyBy = #{modifyBy}, modifyDate = #{modifyDate}, versionId = #{versionId}
where id = #{id}

mapper.select:
SELECT i.*, v.versionNo, c1.categoryName AS categoryLevel1Name, c2.categoryName AS categoryLevel2Name, c3.categoryName AS categoryLevel3Name, d1.valueName AS statusName, d2.valueName AS flatformName
FROM app_info i LEFT JOIN app_version v ON i.versionId = v.id LEFT JOIN app_category c1 ON i.categoryLevel1 = c1.id LEFT JOIN app_category c2 ON i.categoryLevel2 = c2.id LEFT JOIN app_category c3 ON i.categoryLevel3 = c3.id LEFT JOIN data_dictionary d1 ON i.status = d1.valueId LEFT JOIN data_dictionary d2 ON i.flatformId = d2.valueId
WHERE d1.typeCode = "APP_STATUS" AND d2.typeCode = "APP_FLATFORM" AND i.id = #{id}
